import ajax from '@/api'

export default {
  updateAddress: dispatch => {
    return {
       getAddressArray () {
        ajax.user.getUserAddressList().then(res => {
          dispatch({ type: 'SET_ADDRESSARRAY', state: res.data || [] })
          if ((res.data || []).some(v => v.defaultStatus === '0')) {
            dispatch({ type: 'SET_ADDRESS', state: res.data.find(v => v.defaultStatus === '0') })
          }
        })
      }
    }
  }
}
